require 'rails_helper'

RSpec.describe Post, type: :model do
  let(:post) do 
  	FactoryGirl.build(:post)
  end
  context "when creating a new post" do
    it "is valid" do
  	 expect(post).to be_valid
    end
    it "should not have nil content" do
  	 post.content = nil
  	 expect(post).to_not be_valid
    end
    it "should not allow long posts" do
      post.content = ""
      expect(post).to_not be_valid
    end
    it "should not allow user to be nil" do
      post.user = nil
      expect(post).to_not be_valid
    end
  end
end
